	// add body class for page builder
	add_filter('gdlr_core_page_builder_body_class', 'onepagepro_page_builder_body_class');
	if( !function_exists('onepagepro_page_builder_body_class') ){
		function onepagepro_page_builder_body_class( $classes ) {
			return $classes . ' onepagepro-body';
		}
	}
	add_filter('gdlr_core_sidebar_class', 'onepagepro_gdlr_core_sidebar_class');
	if( !function_exists('onepagepro_gdlr_core_sidebar_class') ){
		function onepagepro_gdlr_core_sidebar_class( $classes ) {
			return $classes . ' onepagepro-sidebar-area';
		}
	}

	// Set the neccessary function to be used in the theme
	add_action('after_setup_theme', 'onepagepro_goodlayers_core_setup');
	if( !function_exists( 'onepagepro_goodlayers_core_setup' ) ){
		function onepagepro_goodlayers_core_setup(){

			// to set the goodlayers core plugin pagination
			add_filter('gdlr_core_pagination_style', 'onepagepro_gdlr_core_pagination_style');
			add_filter('gdlr_core_pagination_align', 'onepagepro_gdlr_core_pagination_align');

			// set the default social share option
			add_filter('gdlr_core_social_share_list', 'onepagepro_gdlr_core_social_share_list');

			// widget title navigation
			add_filter('gdlr_core_widget_title_nav', 'onepagepro_gdlr_core_widget_title_nav');

			// single portfolio
			add_filter('gdlr_core_custom_post_slug', 'onepagepro_gdlr_core_custom_post_slug', 10, 2);

			add_filter('gdlr_core_portfolio_single_nav', 'onepagepro_gdlr_core_portfolio_single_nav');
			add_filter('gdlr_core_portfolio_single_nav_same_term', 'onepagepro_gdlr_core_portfolio_single_nav_same_term');
			add_filter('gdlr_core_portfolio_single_related', 'onepagepro_gdlr_core_portfolio_single_related');

			// srcset
			add_filter('gdlr_core_enable_srcset', 'onepagepro_gdlr_core_enable_srcset');

			// filter for portfolio icon hover
			add_filter('gdlr_core_portfolio_hover_icon_class', 'onepagepro_gdlr_core_portfolio_hover_icon_class');

			// youtube cookies
			add_filter('gdlr_core_privacy_settings', 'onepagepro_gdlr_core_privacy_settings');
		}
	}

	// youtube no cookies
	if( !function_exists('onepagepro_gdlr_core_privacy_settings') ){
		function onepagepro_gdlr_core_privacy_settings(){
			if( function_exists('gdlr_core_get_privacy_options') ){
				$privacy_options = gdlr_core_get_privacy_options(1) + gdlr_core_get_privacy_options(2);
				$privacy_settings = array();
				foreach( $privacy_options as $privacy_option_slug => $privacy_option ){
					$privacy_settings[$privacy_option_slug] = onepagepro_get_option('plugin', $privacy_option_slug, 
						(empty($privacy_option['default'])? '': $privacy_option['default']));
				}

				return $privacy_settings;
			}
			
			return array();
		}
	}
	
	// portfolio hover icon class
	if( !function_exists('onepagepro_gdlr_core_portfolio_hover_icon_class') ){
		function onepagepro_gdlr_core_portfolio_hover_icon_class( $type = '' ){
			if( $type == 'image' ){
			 	return onepagepro_get_option('general', 'portfolio-icon-hover-image', '');
			}else if( $type == 'video' ){
				return onepagepro_get_option('general', 'portfolio-icon-hover-video', '');
			}else if( $type == 'link' ){
				return onepagepro_get_option('general', 'portfolio-icon-hover-link', '');
			}
		}
	}

