	// to enable srcset
	if( !function_exists('onepagepro_gdlr_core_enable_srcset') ){
		function onepagepro_gdlr_core_enable_srcset( $enable ){
			$enable_srcset = onepagepro_get_option('plugin', 'enable-srcset', 'disable');
			return ($enable_srcset == 'enable');
		}
	}
			
	// for goodlayers core pagination
	if( !function_exists('onepagepro_gdlr_core_pagination_style') ){
		function onepagepro_gdlr_core_pagination_style( $style ) {
			return onepagepro_get_option('general', 'pagination-style', $style);
		}
	}
	if( !function_exists('onepagepro_gdlr_core_pagination_align') ){
		function onepagepro_gdlr_core_pagination_align( $align ) {
			return onepagepro_get_option('general', 'pagination-align', $align);
		}
	}			

	// custom post type slug
	if( !function_exists('onepagepro_gdlr_core_custom_post_slug') ){
		function onepagepro_gdlr_core_custom_post_slug( $slug, $type = '' ) {
			if( empty($type) ){ $type = $slug; }

			if( $type == 'portfolio' ){
				$slug = onepagepro_get_option('general', 'portfolio-slug', $slug);
			}else if( $type == 'portfolio_category' ){
				$slug = onepagepro_get_option('general', 'portfolio-category-slug', $slug);
			}else if( $type == 'portfolio_tag' ){
				$slug = onepagepro_get_option('general', 'portfolio-tag-slug', $slug);
			}else if( $type == 'personnel' ){
				$slug = onepagepro_get_option('general', 'personnel-slug', $slug);
			}else if( $type == 'personnel_category' ){
				$slug = onepagepro_get_option('general', 'personnel-category-slug', $slug);
			}
			
			return $slug;
		}
	}

	// single portfolio
	if( !function_exists('onepagepro_gdlr_core_portfolio_single_nav') ){
		function onepagepro_gdlr_core_portfolio_single_nav( $check ) {
			return onepagepro_get_option('general', 'enable-single-portfolio-navigation', $check);
		}
	}
	if( !function_exists('onepagepro_gdlr_core_portfolio_single_nav_same_term') ){
		function onepagepro_gdlr_core_portfolio_single_nav_same_term( $check ) {
			return onepagepro_get_option('general', 'enable-single-portfolio-navigation-in-same-tag', $check);
		}
	}
	if( !function_exists('onepagepro_gdlr_core_portfolio_single_related') ){
		function onepagepro_gdlr_core_portfolio_single_related( $args ) {

			$enble_related_portfolio = onepagepro_get_option('general', 'enable-related-portfolio', 'enable');
			if( $enble_related_portfolio == 'enable' ){
				$args['portfolio-style'] = onepagepro_get_option('general', 'related-portfolio-style', 'grid');
				$args['num-fetch'] = onepagepro_get_option('general', 'related-portfolio-num-fetch', 4);
				$args['thumbnail-size'] = onepagepro_get_option('general', 'related-portfolio-thumbnail-size', 'medium');
				$args['excerpt-number'] = onepagepro_get_option('general', 'related-portfolio-num-excerpt', 20);
				$args['column-size'] = onepagepro_get_option('general', 'related-portfolio-column-size', 15);

				return $args;
			}else{
				return array();
			}
		}
	}

	// set default social share option
	if( !function_exists('onepagepro_gdlr_core_social_share_list') ){
		function onepagepro_gdlr_core_social_share_list( $social ) {

			$social['facebook'] = onepagepro_get_option('general', 'blog-social-facebook', $social['facebook']);
			$social['linkedin'] = onepagepro_get_option('general', 'blog-social-linkedin', $social['linkedin']);
			$social['google-plus'] = onepagepro_get_option('general', 'blog-social-google-plus', $social['google-plus']);
			$social['pinterest'] = onepagepro_get_option('general', 'blog-social-pinterest', $social['pinterest']);
			$social['stumbleupon'] = onepagepro_get_option('general', 'blog-social-stumbleupon', $social['stumbleupon']);
			$social['twitter'] = onepagepro_get_option('general', 'blog-social-twitter', $social['twitter']);
			$social['email'] = onepagepro_get_option('general', 'blog-social-email', $social['email']);

			return $social;
		}
	}	

	// add filter to register custom fonts for font lists 
	add_filter('gdlr_core_custom_uploaded_font', 'onepagepro_register_custom_uploaded_font');
	if( !function_exists('onepagepro_register_custom_uploaded_font') ){
		function onepagepro_register_custom_uploaded_font( $custom_fonts ){

			$custom_font = onepagepro_get_option('typography', 'font-upload');
			if( !empty($custom_font) ){
				foreach($custom_font as $font_option){
					$custom_fonts[$font_option['name']] = array(
						'eot' => $font_option['eot'],
						'ttf' => $font_option['ttf'],
					);
				}
			}		
			
			return $custom_fonts;
			
		} // onepagepro_register_custom_uploaded_font
	} // function_exists

	// format widget title
	if( !function_exists('onepagepro_gdlr_core_widget_title_nav') ){
