	// set lightbox type
	add_filter('gdlr_core_lightbox_type', 'onepagepro_gdlr_core_lightbox_type');
	if( !function_exists('onepagepro_gdlr_core_lightbox_type') ){
		function onepagepro_gdlr_core_lightbox_type(){
			$lightbox_type = onepagepro_get_option('plugin', 'lightbox', 'ilightbox');

			if( $lightbox_type == 'ilightbox' ){
				$ilightbox_skin = onepagepro_get_option('plugin', 'ilightbox-skin', 'dark');
				return $lightbox_type . '-' . $ilightbox_skin;
			}else{
				return $lightbox_type;
			}
		}
	}
	add_filter('body_class', 'onepagepro_lightbox_body_class');
	if( !function_exists('onepagepro_lightbox_body_class') ){
		function onepagepro_lightbox_body_class($class){
			$link_to_lightbox = onepagepro_get_option('plugin', 'link-to-lightbox', 'enable');
			if( $link_to_lightbox == 'enable' ){
				$class[] = 'gdlr-core-link-to-lightbox';
			}
			return $class;
		}
	}
	
